mapper.sql:
SELECT m.MATTER_ID matterId, m.MATTER_NAME matterName, m.MATTER_TEXT matterText, m.MATTER_OPEN matterOpen, m.END, m.IS_OPEN isOpen, m.IMPORTANT, m.IS_PATRIARCH isPatriarch, m.URGENT, um.ACTUALLY_TIME, m.PERIOD, m.DEPT_ID deptId, m.CREATE_TIME createTime, d.DEPT_NAME deptName, um.IMPORTANT_ONE importantOne, um.URGENT_ONE urgentOne, um.FINISH,um.SIGN,m.FOR_EACH, LISTAGG(r.REMIND_ID,',') WITHIN GROUP (ORDER BY r.REMIND_ID) AS remindId FROM T_MATTER m LEFT JOIN T_DEPT d ON (m.DEPT_ID = d.DEPT_ID) LEFT JOIN T_REMIND r ON (r.MATTER_ID = m.MATTER_ID) LEFT JOIN T_USER_MATTER um ON (um.MATTER_ID = m.MATTER_ID) LEFT JOIN t_USER u ON (u.USER_ID = um.USER_ID) WHERE 1 = 1
AND m.MATTER_NAME like CONCAT(CONCAT('%',#{tMatter.matterName}),'%')
AND m.IS_PATRIARCH = #{tMatter.isPatriarch}
AND m.DEPT_ID = #{tMatter.deptId}
AND m.IS_OPEN = #{tMatter.isOpen}
AND m.IMPORTANT = #{tMatter.important}
AND m.URGENT = #{tMatter.urgent}
AND m.MATTER_ID = #{tMatter.matterId}
And um.USER_ID = #{tMatter.userId}
And um.FINISH = #{tMatter.finish}
And um.SIGN = #{tMatter.SIGN}
AND TO_CHAR(m.MATTER_OPEN , 'yyyy-MM-dd') = #{tMatter.matterOpenStr}
AND TO_CHAR(m.END , 'yyyy-MM-dd') = #{tMatter.endStr}
GROUP BY m.MATTER_OPEN, m.IS_PATRIARCH , m.END, um.ACTUALLY_TIME, m.MATTER_ID , m.MATTER_NAME, m.MATTER_TEXT, m.IS_OPEN, m.IMPORTANT, m.URGENT, m.PERIOD, m.DEPT_ID, m.CREATE_TIME, d.DEPT_NAME, um.IMPORTANT_ONE, um.URGENT_ONE, um.FINISH,um.SIGN,m.FOR_EACH
order by m.MATTER_OPEN DESC
order by m.CREATE_TIME DESC
order by m.CREATE_TIME
order by m.END DESC
order by m.END
order by um.ACTUALLY_TIME DESC
order by um.ACTUALLY_TIME
