mapper.sql:
SELECT m.MATTER_ID matterId, m.MATTER_NAME matterName, m.MATTER_TEXT matterText, m.MATTER_OPEN matterOpen, m.END, m.IS_OPEN isOpen, m.IMPORTANT, m.URGENT, m.PERIOD, m.DEPT_ID deptId, m.CREATE_TIME createTime, d.DEPT_NAME deptName, um.IMPORTANT_ONE importantOne, um.URGENT_ONE urgentOne, um.FINISH, LISTAGG(r.REMIND_ID,',') WITHIN GROUP (ORDER BY r.REMIND_ID) AS remindId FROM T_MATTER m LEFT JOIN T_DEPT d ON (m.DEPT_ID = d.DEPT_ID) LEFT JOIN T_REMIND r ON (r.MATTER_ID = m.MATTER_ID) LEFT JOIN T_USER_MATTER um ON (um.MATTER_ID = m.MATTER_ID) WHERE 1 = 1
AND m.MATTER_NAME = #{tMatter.matterName}
AND m.DEPT_ID = #{tMatter.deptId}
AND m.IS_OPEN = #{tMatter.isOpen}
AND m.IMPORTANT = #{tMatter.important}
AND m.URGENT = #{tMatter.urgent}
AND m.MATTER_ID = #{tMatter.matterId}
And um.USER_ID = #{tMatter.userId}
And um.FINISH = #{tMatter.finish}
GROUP BY m.MATTER_ID, m.MATTER_NAME, m.MATTER_TEXT, m.MATTER_OPEN, m.END, m.IS_OPEN, m.IMPORTANT, m.URGENT, m.PERIOD, m.DEPT_ID, m.CREATE_TIME, d.DEPT_NAME, um.IMPORTANT_ONE, um.URGENT_ONE, um.FINISH
